		set_time_limit(0);
		$file = fopen("rel.txt", "r");
		$i = 1;
		while(!feof($file)){
			$prod = Product::find($i);
		    $line = fgets($file);
		    $prod->related_products = $line;
		    $prod->save();
		    $i++;
		}
		fclose($file);
		return;
	});
	Route::get('rec', function()
